        private Lock getLock(String id) {
            ReentrantLock lock = lockMap.get(id);
            if (lock == null) {
                synchronized (this) {
                    lock = lockMap.get(id);
    
                    if (lock == null) {
                        lock = new ReentrantLock();
                        lockMap.put(id, lock);
                    }
                }
            }
            return lock;
        }
    
        private void lockExecution(String id, Runnable runnable) {
