    public String Format(Integer number){
        String[] suffix = new String[]{&quot;k&quot;,&quot;m&quot;,&quot;b&quot;,&quot;t&quot;};
        int size = (number.intValue() != 0) ? (int) Math.log10(number) : 0;
        if (size &gt;= 3){
            while (size % 3 != 0) {
                size = size - 1;
            }
