		return digits[2]*10+digits[3];
	}

	/**
	 * Set the current hour
	 * @param hour hour (from 0 to 23)
	 */
	public void setHour(int hour) {
		hour = hour % 24;
		digits[0] = hour/10;
		digits[1] = hour%10;
		updateText();
	}

	/**
	 * Set the current minute
