			writer.write(&quot;PRIVMSG &quot; + Channel + Message);
			writer.flush();
		} catch(IOException ioe){
		   ioe.printStackTrace();
 	    } catch (InterruptedException e) {
           e.printStackTrace();
        } finally {
           writeLock.unlock();
