	public int findPopular(int[] a) {

		if (a == null || a.length == 0)
			return 0;

		Arrays.sort(a);

	    int previous = a[0];
	    int popular = a[0];
	    int count = 1;
	    int maxCount = 1;

	    for (int i = 1; i &lt; a.length; i++) {
			if (a[i] == previous)
				count++;
			else {
				if (count &gt; maxCount) {
					popular = a[i-1];
					maxCount = count;
				}
				previous = a[i];
				count = 1;
			}
		}

	    return count &gt; maxCount ? a[a.length-1] : popular;

	}

