		int ansCount=0;
		int tempCount=0;
		int ans=current;
		for(int i:arr){
			if(i==current){
				tempCount++;
			}
			if(tempCount&gt;ansCount){
				ansCount=tempCount;
				ans=i;
			}
			current=i;
		}
		return ans;
	}

