public class StartTestApplication {


    public static void main(String[] args){
        StartTestApplication testApplication = new StartTestApplication();
        testApplication.runFeature();
    }

    private byte runFeature(){
        Supplier&lt;ClassLoader&gt; classLoader = ClassLoaders::getDefaultClassLoader;
        List&lt;String&gt; arguments = new ArrayList&lt;String&gt;();
        arguments.add(&quot;--glue&quot;);
        arguments.add(&quot;stepDefs&quot;);
        arguments.add(&quot;--glue&quot;);
        arguments.add(&quot;hooks&quot;);
        FeatureWithLines featureWithLines = FeatureWithLines.parse(&quot;src/test/resources/features&quot;);
        RuntimeOptions options = new CommandlineOptionsParser(System.out)
                .parse(arguments.toArray(new String[arguments.size()]))
                .addFeature(featureWithLines)
                .build();

        Runtime runtime = Runtime.builder()
                .withClassLoader(classLoader)
                .withRuntimeOptions(options)
                .build();
        runtime.run();
        System.out.println(runtime.exitStatus());
        return runtime.exitStatus();
    }
}
