    private static class RetryablePredicate implements Predicate {
       private final String[] split;
       private final Term target;
       private int idx;
    
       RetryablePredicate(String[] split, Term target) {
          this.split = split;
          this.target = target;
       }
    
       @Override
       public boolean evaluate() {
          while (idx &lt; split.length) {
             target.backtrack();
             String next = split[idx++];
             if (target.unify(new Atom(next))) {
                return true;
             }
          }
    
          return false;
       }
    
       @Override
       public boolean couldReevaluationSucceed() {
          return idx &lt; split.length;
       }
    }

