    public int solution(int[] H) {
       
            Stack&lt;int&gt; stack = new Stack&lt;int&gt;();

            stack.Push(H[0]);
            int count = 1;



            for (int i = 1; i &lt; H.Length; i++)
            {

                if (H[i] == stack.Peek())
                {
                    continue;
                }
                else if (H[i] &gt; stack.Peek())
                {
                    stack.Push(H[i]);
                    count++;
                }
                else
                {

                    while (!(stack.Count==0) &amp;&amp; H[i] &lt; stack.Peek())
                    {
                        stack.Pop();
                    }
                if (!(stack.Count==0) &amp;&amp; H[i] == stack.Peek()) {
                    continue;
                }


                    stack.Push(H[i]);
                    count++;
                }
            }

