            - &#39;10251:10251&#39;
            - &#39;10252:10252&#39;
            - &#39;10253:10253&#39;
            - &#39;10254:10254&#39;
            - &#39;10255:10255&#39;
            - &#39;10256:10256&#39;
            - &#39;10350:10350&#39;
        environment:
          AZURE_COSMOS_EMULATOR_PARTITION_COUNT: 2
