    // used imports:
    import org.springframework.core.io.ByteArrayResource;
    import org.springframework.http.HttpEntity;
    import org.springframework.http.MediaType;
    import org.springframework.http.client.MultipartBodyBuilder;
    import org.springframework.util.MultiValueMap;

        private MultiValueMap&lt;String, HttpEntity&lt;?&gt;&gt; createMultiPartRelatedBody(YourRequestType request, byte[] content) {
            var multipartBodyBuilder = new MultipartBodyBuilder();
            var filePart = new ByteArrayResource(content); // resource type matters for file type!
            multipartBodyBuilder
                .part(&quot;part 1 with json&quot;, request, MediaType.APPLICATION_JSON)
                .header(&quot;foo&quot;, &quot;bar&quot;);
            multipartBodyBuilder
                .part(&quot;part 2 with file content&quot;, filePart)
                .filename(request.getFileName()) // Only needed if resource type does not expose filename (ByteArrayResource doesn&#39;t)
                .header(&quot;foo&quot;, &quot;bar&quot;);
            return multipartBodyBuilder.build();
        }

So, do NOT use something like:

    .body(BodyInserters.fromMultipartData(builder.build()))

