import java.util.Locale;
import java.util.Map;

@WebServlet ( name = &quot;report&quot;, value = &quot;/&quot; )
public class ReportServlet extends HttpServlet
{
    private String message;

    public void init ( )
    {
        message = &quot;Report&quot;;
    }

    public void doGet ( HttpServletRequest request , HttpServletResponse response ) throws IOException
    {
        response.setContentType( &quot;text/html&quot; );

        // Hello
        PrintWriter out = response.getWriter();
        out.println( &quot;&lt;html lang=&#39;en&#39;&gt;&quot; );
        out.println( &quot;&lt;head&gt;&quot; );
        out.println( &quot;&lt;meta charset=&#39;UTF-8&#39;&gt;&quot; );
        String css =
                &quot;&quot;&quot;
                                
                table, th, td , caption {
                    border: 0.5px solid DarkSlateGrey ;
                }
                table {
                    width: 90% ;
                    border-collapse: collapse ;
                    margin-left: auto;
                    margin-right: auto;
                }
                caption {
                    color: white ; 
                    background-color: DarkSlateGrey ;
                }
                footer {
                    margin-top: 1em ;
                    width: 100% ;
                    text-align: center ;
                    color:  DimGrey ;
                }
                &quot;&quot;&quot;;
        out.println( &quot;&lt;style&gt;%s&lt;/style&gt;&quot;.formatted( css ) );
        out.println( &quot;&lt;body&gt;&quot; );
        out.println( &quot;&lt;h1&gt;&quot; + message + &quot;&lt;/h1&gt;&quot; );

        // Generate report from map data.
        StringBuilder table = new StringBuilder();
        String tableHeader =
                &quot;&quot;&quot;
                &lt;table&gt;
                  &lt;caption&gt;Employee Day-of-Week Report&lt;/caption&gt;
                &quot;&quot;&quot;;
        table.append( tableHeader );
        Object attribute = this.getServletContext().getAttribute( &quot;work.basil.example.employeeWorkDayAssigned&quot; );
        Map &lt; String, DayOfWeek &gt; map = ( Map &lt; String, DayOfWeek &gt; ) attribute; // Cast back to original type.
        for ( Map.Entry &lt; String, DayOfWeek &gt; entry : map.entrySet() )
        {
            String row =
                    &quot;&quot;&quot;
                      &lt;tr&gt;
                        &lt;td&gt;%s&lt;/td&gt;
                        &lt;td&gt;%s&lt;/td&gt;
                      &lt;/tr&gt;
                    &quot;&quot;&quot;
                            .formatted(
                                    entry.getKey() ,
                                    entry.getValue().getDisplayName( TextStyle.FULL_STANDALONE , Locale.US )
                            );
            table.append( row );
        }
        table.append( &quot;&lt;/table&gt;&quot; + &quot;\n&quot; );

        out.println( table.toString() );
        out.println( &quot;&lt;footer&gt;Report generated %s&lt;/footer&gt;&quot;.formatted( Instant.now().truncatedTo( ChronoUnit.MINUTES ).toString() ) );
        out.println( &quot;&lt;/body&gt;&lt;/html&gt;&quot; );
    }

    public void destroy ( )
    {
        // No code needed here.
    }
}
