    con.getOutputStream().write(&quot;Hello&quot;.getBytes());
    con.getOutputStream().flush();
    con.getOutputStream().write(&quot;World&quot;.getBytes());
    con.getOutputStream().flush();
    con.getOutputStream().write(&quot;123&quot;.getBytes());
