     * MediaStore Uris, and other file-based ContentProviders.
     *
     * @param context The context.
     * @param uri The Uri to query.
     * @param selection (Optional) Filter used in the query.
     * @param selectionArgs (Optional) Selection arguments used in the query.
     * @return The value of the _data column, which is typically a file path.
     */
    fun getDataColumn(context: Context, uri: Uri?, selection: String?, selectionArgs: Array&lt;String&gt;?): String? {

        var cursor: Cursor? = null
        val column = MediaStore.Files.FileColumns.DATA
        val projection = arrayOf(column)

        try {
            cursor = context.contentResolver.query(uri!!, projection, selection, selectionArgs, null)
            if (cursor != null &amp;&amp; cursor.moveToFirst()) {
                if (DEBUG)
                    DatabaseUtils.dumpCursor(cursor)

                val columnIndex = cursor.getColumnIndexOrThrow(column)
                return cursor.getString(columnIndex)
            }
        } finally {
            cursor?.close()
        }
        return null
    }

    /**
     * Get a file path from a Uri. This will get the the path for Storage Access
     * Framework Documents, as well as the _data field for the MediaStore and
     * other file-based ContentProviders.&lt;br&gt;&lt;/br&gt;
     * &lt;br&gt;&lt;/br&gt;
     * Callers should check whether the path is local before assuming it
     * represents a local file.
     *
     * @param context The context.
     * @param uri The Uri to query.
     * @see .isLocal
     * @see .getFile
     */
    fun getPath(context: Context, uri: Uri): String? {
        Timber.d(
            &quot;[app] FileHelper - Authority: ${uri.authority}, &quot; +
                &quot;Fragment: ${uri.fragment}, &quot; +
                &quot;Port: ${uri.port}, &quot; +
                &quot;Query: ${uri.query}, &quot; +
                &quot;Scheme: ${uri.scheme}, &quot; +
                &quot;Host: ${uri.host}, &quot; +
                &quot;Segments: ${uri.pathSegments}&quot;
        )

        val isKitKat = Build.VERSION.SDK_INT &gt;= Build.VERSION_CODES.KITKAT

        // DocumentProvider
        if (isKitKat &amp;&amp; DocumentsContract.isDocumentUri(context, uri)) {
            // LocalStorageProvider
            if (isLocalStorageDocument(uri)) {
                // The path is the id
                return DocumentsContract.getDocumentId(uri)
            }
            // ExternalStorageProvider
            else if (isExternalStorageDocument(uri)) {
                val docId = DocumentsContract.getDocumentId(uri)
                val split = docId.split((&quot;:&quot;).toRegex()).dropLastWhile { it.isEmpty() }.toTypedArray()
                val type = split[0]

                if (&quot;primary&quot;.equals(type, ignoreCase = true)) {
                    return (Environment.getExternalStorageDirectory()).toString() + &quot;/&quot; + split[1]
                }
            }
            // DownloadsProvider
            else if (isDownloadsDocument(uri)) {
                val id = DocumentsContract.getDocumentId(uri)
                if (id != null &amp;&amp; id.startsWith(&quot;raw:&quot;)) {
                    return id.substring(4)
                }
                val contentUriPrefixesToTry =
                    arrayOf(&quot;content://downloads/public_downloads&quot;, &quot;content://downloads/my_downloads&quot;)
                for (contentUriPrefix in contentUriPrefixesToTry) {
                    val contentUri =
                        ContentUris.withAppendedId(Uri.parse(contentUriPrefix), java.lang.Long.valueOf(id!!))
                    try {
                        val path = getDataColumn(context, contentUri, null, null)
                        if (path != null) {
                            return path
                        }
                    } catch (e: Exception) {
                    }
                }

                // path could not be retrieved using ContentResolver, therefore copy file to accessible cache using streams
                val fileName = getFileName(context, uri)
                val cacheDir = getDocumentCacheDir(context)
                val file = generateFileName(fileName, cacheDir)
                var destinationPath: String? = null
                if (file != null) {
                    destinationPath = file.absolutePath
                    saveFileFromUri(context, uri, destinationPath)
                }
                return destinationPath
            }
            // MediaProvider
            else if (isMediaDocument(uri)) {
                val docId = DocumentsContract.getDocumentId(uri)
                val split = docId.split((&quot;:&quot;).toRegex()).dropLastWhile { it.isEmpty() }.toTypedArray()
                val type = split[0]

                var contentUri: Uri? = null
                when (type) {
                    &quot;image&quot; -&gt; contentUri = MediaStore.Images.Media.EXTERNAL_CONTENT_URI
                    &quot;video&quot; -&gt; contentUri = MediaStore.Video.Media.EXTERNAL_CONTENT_URI
                    &quot;audio&quot; -&gt; contentUri = MediaStore.Audio.Media.EXTERNAL_CONTENT_URI
                }

                val selection = &quot;_id=?&quot;
                val selectionArgs = arrayOf(split[1])

                return getDataColumn(context, contentUri, selection, selectionArgs)
            }
        } else if (&quot;content&quot;.equals(uri.scheme!!, ignoreCase = true)) {
            // Return the remote address
            if (isGooglePhotosUri(uri)) {
                return uri.lastPathSegment
            }
            return getDataColumn(context, uri, null, null)
        } else if (&quot;file&quot;.equals(uri.scheme!!, ignoreCase = true)) {
            return uri.path
        }

        return null
    }

    /**
     * Convert Uri into File, if possible.
     *
     * @return file A local file that the Uri was pointing to, or null if the
     * Uri is unsupported or pointed to a remote resource.
     * @author paulburke
     * @see .getPath
     */
    fun getFile(context: Context, uri: Uri?): File? {
        if (uri != null) {
            val path = getPath(context, uri)
            if (path != null &amp;&amp; isLocal(path)) {
                return File(path)
            }
        }
        return null
    }

    /**
     * Get the file size in a human-readable string.
     *
     * @param size
     * @return
     * @author paulburke
     */
    fun getReadableFileSize(size: Int): String {
        val bytesInKilobytes = 1024
        val dec = DecimalFormat(&quot;###.#&quot;)
        val kb = &quot; KB&quot;
        val mb = &quot; MB&quot;
        val gb = &quot; GB&quot;
        var fileSize = 0f
        var suffix = kb

        if (size &gt; bytesInKilobytes) {
            fileSize = (size / bytesInKilobytes).toFloat()
            if (fileSize &gt; bytesInKilobytes) {
                fileSize /= bytesInKilobytes
                if (fileSize &gt; bytesInKilobytes) {
                    fileSize /= bytesInKilobytes
                    suffix = gb
                } else {
                    suffix = mb
                }
            }
        }
        return (dec.format(fileSize.toDouble()) + suffix).toString()
    }

    /**
     * Get the Intent for selecting content to be used in an Intent Chooser.
     *
     * @return The intent for opening a file with Intent.createChooser()
     */
    fun createGetContentIntent(): Intent {
        // Implicitly allow the user to select a particular kind of data
        val intent = Intent(Intent.ACTION_GET_CONTENT)
        // The MIME data type filter
        intent.type = &quot;*/*&quot;
        // Only return URIs that can be opened with ContentResolver
        intent.addCategory(Intent.CATEGORY_OPENABLE)
        return intent
    }

    /**
     * Creates View intent for given file
     *
     * @param file
     * @return The intent for viewing file
     */
    fun getViewIntent(context: Context, file: File): Intent {
        // Uri uri = Uri.fromFile(file);
        val uri = FileProvider.getUriForFile(context, AUTHORITY, file)
        val intent = Intent(Intent.ACTION_VIEW)
        val url = file.toString()
        if (url.contains(&quot;.doc&quot;) || url.contains(&quot;.docx&quot;)) {
            // Word document
            intent.setDataAndType(uri, &quot;application/msword&quot;)
        } else if (url.contains(&quot;.pdf&quot;)) {
            // PDF file
            intent.setDataAndType(uri, &quot;application/pdf&quot;)
        } else if (url.contains(&quot;.ppt&quot;) || url.contains(&quot;.pptx&quot;)) {
            // Powerpoint file
            intent.setDataAndType(uri, &quot;application/vnd.ms-powerpoint&quot;)
        } else if (url.contains(&quot;.xls&quot;) || url.contains(&quot;.xlsx&quot;)) {
            // Excel file
            intent.setDataAndType(uri, &quot;application/vnd.ms-excel&quot;)
        } else if (url.contains(&quot;.zip&quot;) || url.contains(&quot;.rar&quot;)) {
            // WAV audio file
            intent.setDataAndType(uri, &quot;application/x-wav&quot;)
        } else if (url.contains(&quot;.rtf&quot;)) {
            // RTF file
            intent.setDataAndType(uri, &quot;application/rtf&quot;)
        } else if (url.contains(&quot;.wav&quot;) || url.contains(&quot;.mp3&quot;)) {
            // WAV audio file
            intent.setDataAndType(uri, &quot;audio/x-wav&quot;)
        } else if (url.contains(&quot;.gif&quot;)) {
            // GIF file
            intent.setDataAndType(uri, &quot;image/gif&quot;)
        } else if (url.contains(&quot;.jpg&quot;) || url.contains(&quot;.jpeg&quot;) || url.contains(&quot;.png&quot;)) {
            // JPG file
            intent.setDataAndType(uri, &quot;image/jpeg&quot;)
        } else if (url.contains(&quot;.txt&quot;)) {
            // Text file
            intent.setDataAndType(uri, &quot;text/plain&quot;)
        } else if ((url.contains(&quot;.3gp&quot;) || url.contains(&quot;.mpg&quot;) || url.contains(&quot;.mpeg&quot;) ||
                url.contains(&quot;.mpe&quot;) || url.contains(&quot;.mp4&quot;) || url.contains(&quot;.avi&quot;))
        ) {
            // Video files
            intent.setDataAndType(uri, &quot;video/*&quot;)
        } else {
            intent.setDataAndType(uri, &quot;*/*&quot;)
        }

        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)
        intent.addFlags(Intent.FLAG_GRANT_READ_URI_PERMISSION)
        intent.addFlags(Intent.FLAG_GRANT_WRITE_URI_PERMISSION)
        return intent
    }

    fun getDocumentCacheDir(context: Context): File {
        val dir = File(context.cacheDir, DOCUMENTS_DIR)
        if (!dir.exists()) {
            dir.mkdirs()
        }
        logDir(context.cacheDir)
        logDir(dir)

        return dir
    }

    private fun logDir(dir: File) {
        if (!DEBUG) return
        Timber.d(&quot;[app] FileHelper log dir=$dir&quot;)
        val files = dir.listFiles()
        for (file in files!!) {
            Timber.d(&quot;[app] FileHelper path:${file.path}&quot;)
        }
    }

    fun generateFileName(name: String?, directory: File): File? {
        var name: String? = name ?: return null

        var file = File(directory, name!!)

        if (file.exists()) {
            var fileName: String = name
            var extension = &quot;&quot;
            val dotIndex = name.lastIndexOf(&#39;.&#39;)
            if (dotIndex &gt; 0) {
                fileName = name.substring(0, dotIndex)
                extension = name.substring(dotIndex)
            }

            var index = 0

            while (file.exists()) {
                index++
                name = &quot;$fileName($index)$extension&quot;
                file = File(directory, name)
            }
        }

        try {
            if (!file.createNewFile()) {
                return null
            }
        } catch (e: IOException) {
            Timber.w(e, &quot;[app] FileHelper&quot;)
            return null
        }

        logDir(directory)

        return file
    }

    /**
     * Writes response body to disk
     *
     * @param body ResponseBody
     * @param path file path
     * @return File
     */
    fun writeResponseBodyToDisk(body: ResponseBody, path: String): File? {
        try {
            val target = File(path)

            var inputStream: InputStream? = null
            var outputStream: OutputStream? = null

            try {
                val fileReader = ByteArray(4096)

                inputStream = body.byteStream()
                outputStream = FileOutputStream(target)

                while (true) {
                    val read = inputStream!!.read(fileReader)

                    if (read == -1) {
                        break
                    }

                    outputStream.write(fileReader, 0, read)
                }

                outputStream.flush()

                return target
            } catch (e: IOException) {
                return null
            } finally {
                if (inputStream != null) {
                    inputStream.close()
                }

                if (outputStream != null) {
                    outputStream.close()
                }
            }
        } catch (e: IOException) {
            return null
        }
    }

    private fun saveFileFromUri(context: Context, uri: Uri, destinationPath: String) {
        var inputStream: InputStream? = null
        var bos: BufferedOutputStream? = null
        try {
            inputStream = context.contentResolver.openInputStream(uri)
            bos = BufferedOutputStream(FileOutputStream(destinationPath, false))
            val buf = ByteArray(1024)
            inputStream!!.read(buf)
            do {
                bos.write(buf)
            } while (inputStream.read(buf) != -1)
        } catch (e: IOException) {
            e.printStackTrace()
        } finally {
            try {
                inputStream?.close()
                bos?.close()
            } catch (e: IOException) {
                e.printStackTrace()
            }
        }
    }

    fun readBytesFromFile(filePath: String): ByteArray? {
        var fileInputStream: FileInputStream? = null
        var bytesArray: ByteArray? = null

        try {
            val file = File(filePath)
            bytesArray = ByteArray(file.length().toInt())

            // read file into bytes[]
            fileInputStream = FileInputStream(file)
            fileInputStream.read(bytesArray)
        } catch (e: IOException) {
            e.printStackTrace()
        } finally {
            if (fileInputStream != null) {
                try {
                    fileInputStream.close()
                } catch (e: IOException) {
                    e.printStackTrace()
                }
            }
        }

        return bytesArray
    }

    @Throws(IOException::class)
    fun createTempImageFile(context: Context, fileName: String): File {
        // Create an image file name
        val storageDir = File(context.cacheDir, DOCUMENTS_DIR)
        return File.createTempFile(fileName, &quot;.jpg&quot;, storageDir)
    }

    fun getFileName(context: Context, uri: Uri): String? {
        val mimeType = context.contentResolver.getType(uri)
        var filename: String? = null

        if (mimeType == null) {
            val path = getPath(context, uri)
            if (path == null) {
                filename = getName(uri.toString())
            } else {
                val file = File(path)
                filename = file.name
            }
        } else {
            val returnCursor = context.contentResolver.query(uri, null, null, null, null)
            if (returnCursor != null) {
                val nameIndex = returnCursor.getColumnIndex(OpenableColumns.DISPLAY_NAME)
                returnCursor.moveToFirst()
                filename = returnCursor.getString(nameIndex)
                returnCursor.close()
            }
        }

        return filename
    }

    fun getName(filename: String?): String? {
        if (filename == null) {
            return null
        }
        val index = filename.lastIndexOf(&#39;/&#39;)
        return filename.substring(index + 1)
    }
