    public class SearchLinkedList&lt;E&gt; {
    private Node&lt;E&gt; first;

    public static void main(String[] args) {
        SearchLinkedList&lt;Integer&gt; list = new SearchLinkedList&lt;Integer&gt;();

        list.insert(1000);
        list.insert(2000);
        list.insert(3000);

