                case 38:
                    builder.append(&quot;&amp;amp;&quot;);
                    break;
                case 60:
                    builder.append(&quot;&amp;lt;&quot;);
                    break;
                case 62:
                    builder.append(&quot;&amp;gt;&quot;);
                    break;
                default:
                    builder.append(String.valueOf(c));
                }
            }
        }
