private boolean isBSTRecursively(Node node, int min, int max) {
    return node == null
           || node.getValue() &gt;= min &amp;&amp; node.getValue() &lt;= max
               &amp;&amp; isBSTRecursively(node.getLeft(), min, node.getValue())
               &amp;&amp; isBSTRecursively(node.getRight(), node.getValue(), max);           
}
