        BuildGUI();
    }
    
    public void BuildGUI()
    {
        setSize(1200, 800);
        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        setLayout(new GridLayout(10, 10));

        for(int r = 0; r &lt; grid.length; r++)
        {
            for(int c = 0; c &lt; grid[r].length; c++)
            {
