                grid[r][c].setActionCommand(r + &quot;:&quot; + c);
                grid[r][c].addMouseListener(this); // CHANGE HERE
                getContentPane().add(grid[r][c]);
            }
        }
        setVisible(true);
    }

    public void mouseClicked(MouseEvent b)
