    	log.info(&quot;Connected to server .&quot;);
    	log.info(ftp.getReplyString());
    
    	ftp.changeWorkingDirectory(remoteDest);
    	ftp.setFileType(FTPClient.BINARY_FILE_TYPE);
    
    	try {
    		upload(localSrc, ftp);
    	}
    	finally {
    		ftp.disconnect();
    		log.info(&quot;FTP disconnected&quot;);			
    	}
    }
    
    public void upload(File src, FTPClient ftp) throws IOException {
    	if (src.isDirectory()) {
    		ftp.makeDirectory(src.getName());
    		ftp.changeWorkingDirectory(src.getName());
    		for (File file : src.listFiles()) {
    			upload(file, ftp);
    		}
    		ftp.changeToParentDirectory();
    	}
    	else {
    		InputStream srcStream = null;
    		try {
    			srcStream = src.toURI().toURL().openStream();
    			ftp.storeFile(src.getName(), srcStream);
    		}
    		finally {
    			IOUtils.closeQuietly(srcStream);
    		}
    	}
    }

[`IOUtils`](http://commons.apache.org/io/api-release/org/apache/commons/io/IOUtils.html) is a part of [Apache Commons IO](http://commons.apache.org/io/).

