    private static void helper(int[] array, int pos){  
        if(pos &gt;= array.length - 1){   
            System.out.print(&quot;[&quot;);  
            for(int i = 0; i &lt; array.length - 1; i++){  
                System.out.print(array[i] + &quot;, &quot;);  
