            System.out.println(&quot;]&quot;);  
            return;  
        }  
  
        for(int i = pos; i &lt; array.length; i++){   
          
            int t = array[pos];  
            array[pos] = array[i];  
            array[i] = t;  
  
            helper(array, pos+1);  
  
            t = array[pos];  
            array[pos] = array[i];  
            array[i] = t;  
        }  
    }  
    public static void main(String args[]) {  
        int[] numbers = {1, 2, 3};  
        getPermutations(numbers);  
    }  
