    			double distance = Math.sqrt(dx * dx + dy * dy);
    
    			this.velocity.x -= (this.position.x - Mouse.getX()) / distance;
    			this.velocity.y -= (this.position.y - (Main.HEIGHT - Mouse.getY())) / distance;
