            mDatabase.execSQL(insertSQL, new String[]{eName, eDept, eJoiningDate, eSalary});

            Toast.makeText(this, &quot;Employee Added Successfully&quot;, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
