    if(!compareTwoCells(cell1, cell2)) {
      equalRows = false;
      System.out.println(&quot;       Cell &quot;+i+&quot; - NOt Equal&quot;);
      setCellColor(cell1, IndexedColors.YELLOW );
      setCellColor(cell2, IndexedColors.YELLOW );
    }
    ...

