        static Pattern SPLIT_CAMEL_CASE = Pattern.compile(
            BETWEEN_LOWER_AND_UPPER +&quot;|&quot;+ BEFORE_UPPER_AND_LOWER
        );

        public static String splitCamelCase(String s) {
