			case MotionEvent.ACTION_DOWN:
				pointerId = event.getPointerId(0);
				lastPointerX = event.getX();
				lastPointerY = event.getY();
				invalidate(); //This method will call onDraw().
				return true;
			case MotionEvent.ACTION_MOVE:
				pointerIndex = event.findPointerIndex(pointerId);
				pointerX = event.getX(pointerIndex);
				pointerY = event.getY(pointerIndex);
				distanceX = pointerX - lastPointerX;
				distanceY = pointerY - lastPointerY;
				invalidate();
				return true;
			case MotionEvent.ACTION_POINTER_UP:
				//Set to the oldest touch pointer
				int pointerIndex = (event.getAction() &amp; MotionEvent.ACTION_POINTER_INDEX_MASK) &gt;&gt; MotionEvent.ACTION_POINTER_INDEX_SHIFT;
				final int pointerId = event.getPointerId(pointerIndex);
				if(pointerId == this.pointerId) {
					if(pointerIndex == 0) { pointerIndex++;
					} else { pointerIndex = 0; }
					this.pointerId = event.getPointerId(pointerIndex);
					lastPointerX = event.getX(pointerIndex) - distanceX;
					lastPointerY = event.getY(pointerIndex) - distanceY;
				}
				return true;
			case MotionEvent.ACTION_UP:
				return true;
		}
		return super.onTouchEvent(event);
	}
}
