	public Sample(String name, boolean flag) {
		this.name = name;
		this.flag = flag;
	}

	private String name;

	@JsonSerialize(using = NumericBooleanSerializer.class)
	@JsonDeserialize(using = NumericBooleanDeserializer.class)
