    public BigInteger expMod(BigInteger n, BigInteger a, BigInteger t) {
        // case t = 0
        BigInteger res;
        if  (t.equals(BigInteger.ZERO)) {
            res = BigInteger.ONE;
        }

        // case t =1
        else if (t.equals(BigInteger.ONE)) {
            res = a.mod(n);
