    public class QuickSort implements SortInterface {  
     
        private static int count = 0;
        private static long timeStart = 0;
        private static long timeEnd = 0;
     
        private static void swap(int[] arr, int i, int j) {
            int temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
    	public static int partition(int[] array, int low, int high) {
     
    		int pivot = array[high];
    		int i = (low - 1);
     
    		for (int j = low; j &lt;= high - 1; j++) {
    			if(array[j] &lt;= pivot) {
    				i++;
    				swap(array, i, j);
    			}
    		}
    		swap(array, i + 1, high);
    		return i + 1;	    
        }
     
    	//  function to recursively sort the list
    	public int[] recursiveSort(int[] array, int low, int high) throws UnsortedException {
    		count++;
        	timeStart = System.nanoTime();
        	int i = partition(array, low, high);
     
        	if(low &lt; i - 1) {
        		recursiveSort(array, low, i - 1);
        	}
     
        	if(high &gt; i) {
        		recursiveSort(array, i, high);
        	}
    	    timeEnd = System.nanoTime();
    	    return array;
        }
     
    	public int[] iterativeSort(int[] array, int low, int high) throws UnsortedException {
    		count++;
        	timeStart = System.nanoTime();
    		int stack[] = new int[high - 1 + 1];
    		int top = -1;
     
    		stack[++top] = low;
    		stack[++top] = high;
     
    		while (top &gt;= 0) {
    			high = stack[top--];
    			low = stack[top--];
     
    			int pivot = partition(array, low, high);
     
    			if(pivot - 1 &gt; 1) {
    				stack[++top] = 1;
    				stack[++top] = pivot - 1;
    			}
     
    			if(pivot + 1 &lt; high) {
    				stack[++top] = pivot + 1;
    				stack[++top] = high;
    			}
    		}
    	    timeEnd = System.nanoTime();
    	    return array;
        }
     
