public static ArrayList&lt;Integer&gt; longestConsecutiveIncreasingSequence(int[] array) {
        if (array.length == 0)
            return new ArrayList&lt;&gt;();
        
        int max_sequence_length = 0;
        int max_sequence_start_index = 0;
        int current_sequence_start_index = 0;
        int current_sequence_length = 1;
        
        for (int index = 0; index &lt; array.length; ++index) {
            if (index + 1 &lt; array.length) {
                if (array[index] &lt; array[index + 1]) {
                    ++current_sequence_length;
                } else {
                    if (max_sequence_length &lt; current_sequence_length) {
                        max_sequence_length = current_sequence_length;
                        max_sequence_start_index = current_sequence_start_index;
                    }
                    current_sequence_start_index = index + 1;
                    current_sequence_length = 1;
                }
            }
        }
        if (max_sequence_length &lt; current_sequence_length) {
            max_sequence_length = current_sequence_length;
            max_sequence_start_index = current_sequence_start_index;
        }
        
        ArrayList&lt;Integer&gt; sequence = new ArrayList&lt;&gt;();
        for (int index = max_sequence_start_index; index &lt; max_sequence_start_index + max_sequence_length; ++index)
            sequence.add(array[index]);
        return sequence;
    }
