        public static int[] removeDuplicates(int[] numbers) {
        Entry[] entries = new Entry[numbers.length];
        int size = 0;
        for (int i = 0 ; i &lt; numbers.length ; i++) {
            int nextVal = numbers[i];
            int index = nextVal % entries.length;
            Entry e = entries[index];
            if (e == null) {
                entries[index] = new Entry(nextVal);
                size++;
            } else {
                if(e.insert(nextVal)) {
                    size++;
                }
            }
        }
        int[] result = new int[size];
        int index = 0;
        for (int i = 0 ; i &lt; entries.length ; i++) {
            Entry current = entries[i];
            while (current != null) {
                result[i++] = current.value;
                current = current.next;
            }
        }
        return result;
    }

    public static class Entry {
        int value;
        Entry next;

        Entry(int value) {
            this.value = value;
        }

        public boolean insert(int newVal) {
            Entry current = this;
            Entry prev = null;
            while (current != null) {
                if (current.value == newVal) {
                    return false;
                } else if(current.next != null) {
                    prev = current;
                    current = next;
                }
            }
            prev.next = new Entry(value);
            return true;
        }
    }

