    public class CircularList {
    
        private Node list;
    
        public reversePrint() {
            Stack&lt;Node&gt; stack = new Stack&lt;Node&gt;();
    
            // First, put all the entries in the stack
            Node node = list;
            do {
                stack.push(node);
                node = node.getNext();
            } while (node != list);
    
            while (!stack.empty()) {
                System.out.print(stack.pop());
            }
        }
    
    }

