    public class ReversePrinter {
    
        private Node&lt;?&gt; head;
        private Node&lt;?&gt; current;
        private boolean first = true;
    
        public ReversePrinter(Node&lt;?&gt; head) {
            this.head = head;
            this.current = head;
        }
    
        public void printReverse() {

            if (current == null) {
                return;
            } else if (current == head) {
        		if (!first) return;
        		first = false;
        	}
    
            Node&lt;?&gt; previous = current;
            current = current.getNext();
            printReverse();
