            List&lt;Integer&gt; newRankedBoard = new ArrayList&lt;&gt;();
            newRankedBoard.add(ranked.get(0));
            
            for(int i = 1 ; i &lt; ranked.size() ; i++){
                if(ranked.get(i-1) != ranked.get(i)){
                    newRankedBoard.add(ranked.get(i));
                }
            }        
            for(Integer point : player){
                int newRanked = findNewRankedPosition(newRankedBoard, point);
                newLeadRank.add(newRanked);
            }
            return newLeadRank;
