        public static int findNewRankedPosition(List&lt;Integer&gt; newRankedBoard, int playerPoint){
            int last = newRankedBoard.size()-1;
            int first = 0;
            return binarySearch(newRankedBoard, first, last, playerPoint);
        }
        
        public static int binarySearch(List&lt;Integer&gt; newRankedBoard, int first, int last, int key){
            int mid = (first + last) / 2;
            int newRanked = 1;
            while(first &lt;= last){
                if(newRankedBoard.get(mid) &gt; key){
                    
                    if(mid + 1 &lt; newRankedBoard.size() &amp;&amp; newRankedBoard.get(mid+1) &lt; key){
                        
                        newRanked = mid + 2;
                        break;
                    }else if(mid + 1 == newRankedBoard.size()){
                        newRanked = mid + 2;
                        break;
                    }
                    return binarySearch(newRankedBoard, mid + 1, last, key);  
                }else if(newRankedBoard.get(mid) &lt; key){
                    if(mid - 1 &gt; 0 &amp;&amp; newRankedBoard.get(mid-1) &gt; key){
                        newRanked = mid + 1;
                        break;
                    }else if(mid - 1 == 0 &amp;&amp; newRankedBoard.get(0) &lt; key){
                        newRanked = 1;
                        break;
                    }
                    return binarySearch(newRankedBoard, first, mid - 1, key);
                }else if(newRankedBoard.get(mid) == key){
                    newRanked = mid + 1;
                    break; 
                }
            }
