    public static int removeEvenNumbers(Stack&lt;Integer&gt; stack, int k) {
        if (k &lt;= 0 || stack.empty()) {
            return 0;
        }
        if (stack.peek() % 2 == 0) {
            stack.pop();
            return k &gt; 1 ? removeEvenNumbers(stack, k - 1) : 1;
        } else {
            int removed = stack.pop();
            int returns = removeEvenNumbers(stack, k);
