    File folder= new File(url.getFile());	
    VelocityEngine ve = new VelocityEngine();
           
    ve.setProperty(Velocity.FILE_RESOURCE_LOADER_PATH, folder.getAbsolutePath());
    ve.init();
    
    Template template = ve.getTemplate( &quot;MyTemplate.vm&quot; );

