            nfcAdapter.enableForegroundDispatch(this, pendingIntent, writeTagFilters, null);
        }
        /******************************************************************************
         **********************************Disable Write*******************************
         ******************************************************************************/
        private void WriteModeOff(){
            writeMode = false;
            nfcAdapter.disableForegroundDispatch(this);
        }
