    import edu.princeton.cs.algs4.StdOut;
    import edu.princeton.cs.algs4.WeightedQuickUnionUF;
    
    public class Percolation {
    private boolean[][] grid; // grid
    private WeightedQuickUnionUF wqfGrid; // quick union for grid
    private WeightedQuickUnionUF wqfFull; // quick union for full
    private int gridSize; // grid size
    private int virtualTop; // top virtual site
    private int virtualBottom; // bottom virtual site
    private int openSites; // number of open sites
    
    // creates n-by-n grid, with all sites initially blocked
    public Percolation(int n) {
    if (n &lt;= 0) throw new IllegalArgumentException();
    gridSize = n;
    grid = new boolean[gridSize][gridSize];
    wqfGrid = new WeightedQuickUnionUF(n * n + 2);
    wqfFull = new WeightedQuickUnionUF(n * n + 1);
    virtualBottom = n * n + 1;
    virtualTop = n * n;
    openSites = 0;
    }
    
    // opens the site (row, col) if it is not open already
    public void open(int row, int col) {
    validateSite(row, col);
    
    int shiftRow = row - 1;
    int shiftCol = col - 1;
    int flatIndex = flattenGrid(row, col) - 1;
    
    // if site is already open, stop
    if (isOpen(row, col)) {
    return;
    }
    
    // open site
    grid[shiftRow][shiftCol] = true;
    openSites++;
    
    if (row == 1) { // top row
    wqfGrid.union(virtualTop, flatIndex);
    wqfFull.union(virtualTop, flatIndex);
    }
    
    if (row == gridSize) { // bottom row
    wqfGrid.union(virtualBottom, flatIndex);
    }
    
    // check and open site on left
    if (isOnGrid(row, col - 1) &amp;&amp; isOpen(row, col - 1)) {
    wqfGrid.union(flatIndex, flattenGrid(row, col - 1) - 1);
    wqfFull.union(flatIndex, flattenGrid(row, col - 1) - 1);
    }
    
    // check and open site on right
    if (isOnGrid(row, col + 1) &amp;&amp; isOpen(row, col + 1)) {
    wqfGrid.union(flatIndex, flattenGrid(row, col + 1) - 1);
    wqfFull.union(flatIndex, flattenGrid(row, col + 1) - 1);
    }
    
    // check and open site on top (up)
    if (isOnGrid(row - 1, col) &amp;&amp; isOpen(row - 1, col)) {
    wqfGrid.union(flatIndex, flattenGrid(row - 1, col) - 1);
    wqfFull.union(flatIndex, flattenGrid(row - 1, col) - 1);
    }
    
    // check and open site on bottom (down)
    if (isOnGrid(row + 1, col) &amp;&amp; isOpen(row + 1, col)) {
    wqfGrid.union(flatIndex, flattenGrid(row + 1, col) - 1);
    wqfFull.union(flatIndex, flattenGrid(row + 1, col) - 1);
    }
    }
    
    // is the site (row, col) open?
    public boolean isOpen(int row, int col) {
    validateSite(row, col);
    return grid[row - 1][col - 1];
    
    }
    
    // is the site (row, col) full?
    public boolean isFull(int row, int col) {
    validateSite(row, col);
    return wqfFull.find(virtualTop) == wqfFull.find(flattenGrid(row, col) - 1);
    }
    
    // returns the number of open sites
    public int numberOfOpenSites() {
    return openSites;
    }
    
    // does the system percolate?
    public boolean percolates() {
    return wqfGrid.find(virtualTop) == wqfGrid.find(virtualBottom);
    }
    
    // unit testing (required)
    public static void main(String[] args) {
    int size = Integer.parseInt(args[0]);
    
    Percolation percolation = new Percolation(size);
    int argCount = args.length;
    for (int i = 1; argCount &gt;= 2; i += 2) {
