      arrays ={3,3,3}

Easy solution :

    static int equalizeArray(int N, int k, int arr[])
    {
    
       long   steps=0;
       Arrays.sort(arr);
       int mid=0;
       if(N%2==1){
           mid=N/2;
       }
       else{
           mid=N/2-1;
       }
       for( int i=0;i&lt;N;i++){
   
           if(  (Math.abs(arr[i]-arr[mid]))%k!=0){
               return -1;
           }
       }
       for( int i=0;i&lt;N;i++){
       long   diff=Math.abs(arr[i]-arr[mid]);
           steps+=(diff/k)%1000000007;
       }
       return (int)steps%1000000007;
       
    }

