	public &lt;T&gt; List&lt;Class&lt;? extends T&gt;&gt; findAllMatchingTypes(Class&lt;T&gt; toFind) {
		foundClasses = new ArrayList&lt;Class&lt;?&gt;&gt;();
		List&lt;Class&lt;? extends T&gt;&gt; returnedClasses = new ArrayList&lt;Class&lt;? extends T&gt;&gt;();
		this.toFind = toFind;
		walkClassPath();
		for (Class&lt;?&gt; clazz : foundClasses) {
