    static int highestNumberBelowNDivisibleByTwoNumbers(int firstDivisor, int secondDivisor, int n) {
        while (n&gt;0) {
            if ((n % firstDivisor == 0) &amp;&amp; (n % secondDivisor == 0)) {
                return n;
            }
            n--;
        }
        return -1; // no answer found      
