    		//IntStream intStream = Arrays.stream(A).filter(x -&gt; x &gt;= 0).distinct().sorted();
    		int[] B = Arrays.stream(A).filter(x -&gt; x &gt; 0).distinct().sorted().toArray();
    		for (int i = 0, index = 1; i &lt; B.length; i++, index++) {
    			if (index != B[i]) {
    				return index;
    			}
    		}
    		return B.length + 1;
    	}
    }

