                    private int compareRecursively(final JsonNode o1, final JsonNode o2) {
                        if (o1.isObject() &amp;&amp; o2.isObject()) {
                            return compareObjectNodes(o1, o2);
                        } else if (o1.isObject()) {
                            return 1;
                        } else if (o2.isObject()) {
