                for (TextWatcher textWatcher : textWatcherList) textWatcher.afterTextChanged(s);

            }
        });


        b.etText.setOnEditorActionListener((v, actionId, event) -&gt; {
            if (actionId == EditorInfo.IME_ACTION_DONE) {
                b.etText.clearFocus();
