            b.tvHint.animate().translationY(0).translationX(0).scaleX(1f).scaleY(1f);
        }
    }

    public void setText(String text) {
        b.etText.setText(text);
        if (text != null &amp;&amp; !text.isEmpty()) triggerAnimation(true);
