        double num = -1; // initialized to a negative number to ensure the loop runs at least once
        while (num &lt; 0) {
            if (!in.hasNextDouble()) {
                String input = in.next();
                System.err.println(input + &quot; is not a number, try again please&quot;);
            } else {
