    public &lt;T&gt; Stream&lt;T&gt; toReverseStream(List&lt;T&gt; list) {
		ListIterator&lt;T&gt; listIterator = list.listIterator(list.size());
		return Stream.generate(listIterator::previous)
				.limit(list.size());
	}
