                // All these other arraylists are used to store object of a donor person
                allPPLEmails.add(doc.get(&quot;number&quot;).toString());
                allPPLNames.add(doc.get(&quot;Name&quot;).toString());
                allPPLImages.add(doc.get(&quot;image&quot;).toString());
